output_format:
{
  "$schema": "https://json-schema.org/draft/2020-12/schema",
  "title": "CountryList",
  "type": "array",
  "items": {
    "type": "object",
    "properties": {
      "country": {
        "type": "string",
        "description": "Name of the country"
      },
      "capital": {
        "type": "string",
        "description": "Capital of the country"
      },
      "languages": {
        "type": "array",
        "items": {
          "type": "string",
          "description": "Name of a language"
        },
        "minItems": 1,
        "description": "List of spoken languages"
      }
    },
    "required": ["country", "capital", "languages"],
    "additionalProperties": false
  }
}

