rag_json:
{
  "type": "function",
  "function": {
    "name": "get_text",
    "description": "Extracts relevant keywords, synonyms, and abbreviations from a user query for text search. Returns 3–6 search terms optimized for semantic or full-text search.",
    "parameters": {
      "type": "object",
      "properties": {
        "words": {
          "type": "array",
          "description": "List of 3–6 relevant search terms, including synonyms, abbreviations, and related concepts.",
          "items": {
            "type": "string",
            "description": "A single search term or synonym."
          },
          "minItems": 3,
          "maxItems": 6
        }
      },
      "required": ["words"],
      "additionalProperties": false
    }
  }
}
