tools_python_code:
import sys
import json
import requests

API_KEY = "ac85d9cdcaef65f8c3130a7d5d5a0fde"

USE_PROXY = False  # ‚Üê This variable controls proxy usage

PROXIES = {
    "http": "http://barnstorfr:MonalisaLinux!234@ndrprx01.ndr-net.de:8080",
    "https": "http://barnstorfr:MonalisaLinux!234@ndrprx01.ndr-net.de:8080"
} if USE_PROXY else None

def get_weather(city):
    url = "https://api.openweathermap.org/data/2.5/weather"
    # Changed lang to "en" for consistent English output
    params = {"q": city, "appid": API_KEY, "units": "metric", "lang": "en"}
    response = requests.get(url, params=params, proxies=PROXIES)
    return response.json()

def get_forecast(city):
    url = "https://api.openweathermap.org/data/2.5/forecast"
    # Changed lang to "en" for consistent English output
    params = {"q": city, "appid": API_KEY, "units": "metric", "lang": "en"}
    response = requests.get(url, params=params, proxies=PROXIES)
    return response.json()

# Process command line argument
if len(sys.argv) != 2:
    print("Please provide a JSON argument.")
    sys.exit(1)

try:
    json_input = json.loads(sys.argv[1])
    name = json_input.get("name")
    city = json_input.get("arguments", {}).get("city")

    if not city:
        print("City name is missing in the argument.")
        sys.exit(1)

    if name == "get_weather":
        result = get_weather(city)
    elif name == "get_forecast":
        result = get_forecast(city)
    else:
        print("Invalid function name. Use 'get_weather' or 'get_forecast'.")
        sys.exit(1)

    print(json.dumps(result, indent=2, ensure_ascii=False))

except Exception as e:
    print(f"Error processing JSON: {e}")
